+ (NSDictionary *)generateMerchantInfoWithMerchantId:(NSString *)merchantId agentMerchantId:(NSString *)agentMerchantId {
    if (agentMerchantId.length == 15) {
        return [self generateMerchantInfoWithAgentMerchantId:agentMerchantId];
    }
    else {
        return [self generateMerchantInfoWithMerchantId:merchantId];
    }
}

+ (NSString *)generateMerOrderId {
    NSDate *date = [NSDate date];
    NSDateFormatter *formatter = [[NSDateFormatter alloc] init];
    formatter.dateFormat = @"YYMMddHHmmss";
    NSString *merOrderId = [formatter stringFromDate:date];
    
    return merOrderId;
}

+ (NSString *)fenFromYuan:(NSString *)yuan {
    float fYuan = [yuan floatValue];
    int fFen = (int)(fYuan * 100);
    return [NSString stringWithFormat:@"%i", fFen];
}

